const page=document.querySelector("body"),radioAndButtons=document.querySelectorAll(".header__item"),selects=document.querySelector(".header__selects"),updateControls=e=>{selects.value=e,"light-theme"==selects.value?radioAndButtons[0].checked=!0:"dark-theme"==selects.value?radioAndButtons[1].checked=!0:radioAndButtons[2].checked=!0},selectTheme=e=>{page.setAttribute("class",""),page.classList.add(e),updateControls(e)};radioAndButtons.forEach(e=>{e.addEventListener("click",e=>{const t=e.target.dataset.theme;selectTheme(t)})}),selects.addEventListener("change",e=>{const t=e.target,s=t[t.selectedIndex].value;selectTheme(s)});